import React, { useState } from "react";
import axios from "axios";

function AddTravel() {
  const [formData, setFormData] = useState({
    country: "",
    duration: "",
    cost: "",
    distance: "",
    month: "",
    year: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3001/api/travels", formData);
      alert("Viaje agregado exitosamente.");
      setFormData({ country: "", duration: "", cost: "", distance: "", month: "", year: "" });
    } catch (error) {
      console.error("Error al agregar el viaje:", error);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Agregar Viaje</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="country" placeholder="País" value={formData.country} onChange={handleChange} required />
        <input type="number" name="duration" placeholder="Duración (días)" value={formData.duration} onChange={handleChange} required />
        <input type="number" name="cost" placeholder="Costos" value={formData.cost} onChange={handleChange} required />
        <input type="number" name="distance" placeholder="Kilómetros recorridos" value={formData.distance} onChange={handleChange} required />
        <input type="number" name="month" placeholder="Mes" value={formData.month} onChange={handleChange} required />
        <input type="number" name="year" placeholder="Año" value={formData.year} onChange={handleChange} required />
        <button type="submit">Agregar</button>
      </form>
    </div>
  );
}

export default AddTravel;
