import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

function EditComment() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ country: "", comment: "" });

  useEffect(() => {
    const fetchComment = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/comments/${id}`);
        setFormData(response.data);
      } catch (error) {
        console.error("Error al cargar el comentario:", error);
      }
    };
    fetchComment();
  }, [id]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3001/api/comments/${id}`, formData);
      alert("Comentario actualizado exitosamente.");
      navigate("/view-travels");
    } catch (error) {
      console.error("Error al actualizar el comentario:", error);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Editar Comentario</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="country" value={formData.country} onChange={handleChange} required />
        <textarea name="comment" value={formData.comment} onChange={handleChange} required></textarea>
        <button type="submit">Actualizar</button>
      </form>
    </div>
  );
}

export default EditComment;
