import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

function EditTravel() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    country: "",
    duration: "",
    cost: "",
    distance: "",
    month: "",
    year: "",
  });

  useEffect(() => {
    const fetchTravel = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/travels/${id}`);
        setFormData(response.data);
      } catch (error) {
        console.error("Error al cargar el viaje:", error);
      }
    };
    fetchTravel();
  }, [id]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3001/api/travels/${id}`, formData);
      alert("Viaje actualizado exitosamente.");
      navigate("/view-travels");
    } catch (error) {
      console.error("Error al actualizar el viaje:", error);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Editar Viaje</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="country" value={formData.country} onChange={handleChange} required />
        <input type="number" name="duration" value={formData.duration} onChange={handleChange} required />
        <input type="number" name="cost" value={formData.cost} onChange={handleChange} required />
        <input type="number" name="distance" value={formData.distance} onChange={handleChange} required />
        <input type="number" name="month" value={formData.month} onChange={handleChange} required />
        <input type="number" name="year" value={formData.year} onChange={handleChange} required />
        <button type="submit">Actualizar</button>
      </form>
    </div>
  );
}

export default EditTravel;
