import React, { useState } from 'react';

const AddTrip = () => {
  const [trip, setTrip] = useState({
    pais: '',
    ano: '',
    mes: '',
    duracion: '',
    costo: '',
    kmRecorrido: ''
  });

  const handleChange = (e) => {
    setTrip({ ...trip, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:3003/viajes', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(trip)
    });
    if (response.ok) {
      alert('Viaje agregado exitosamente');
    } else {
      alert('Error agregando el viaje');
    }
  };

  return (
    <div>
      <h2>Agregar Viaje</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="pais" placeholder="País" value={trip.pais} onChange={handleChange} required />
        <input type="number" name="ano" placeholder="Año" value={trip.ano} onChange={handleChange} required />
        <input type="number" name="mes" placeholder="Mes" value={trip.mes} onChange={handleChange} required />
        <input type="number" name="duracion" placeholder="Duración" value={trip.duracion} onChange={handleChange} required />
        <input type="number" name="costo" placeholder="Costo" value={trip.costo} onChange={handleChange} required />
        <input type="number" name="kmRecorrido" placeholder="Kilómetros Recorridos" value={trip.kmRecorrido} onChange={handleChange} required />
        <button type="submit">Agregar</button>
      </form>
    </div>
  );
};

export default AddTrip;
