import React, { useState } from 'react';
import axios from 'axios';

function AgregarComentario() {
  const [comentario, setComentario] = useState({
    pais: '',
    comentario: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setComentario({ ...comentario, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3001/api/comentarios', comentario);
      alert('Comentario agregado correctamente.');
    } catch (err) {
      alert('Error al agregar el comentario.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Pais:</label>
      <input name="pais" value={comentario.pais} onChange={handleChange} />
      <label>Comentario:</label>
      <input name="comentario" value={comentario.comentario} onChange={handleChange} />
      <button type="submit">Agregar Comentario</button>
    </form>
  );
}

export default AgregarComentario;
