// frontend/src/services/sendFormSql.js
const BASE_URL = "http://localhost:3001/trips"; // URL base del backend para la entidad trips

// Agregar un nuevo viaje
const addTrip = async (trip) => {
  const response = await fetch(BASE_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(trip),
  });

  if (!response.ok) {
    throw new Error("Error al agregar el viaje");
  }

  return await response.json();
};

// Obtener todos los viajes
const getTrips = async () => {
  const response = await fetch(BASE_URL);
  if (!response.ok) {
    throw new Error("Error al obtener los viajes");
  }
  return await response.json();
};

// Eliminar un viaje por ID
const deleteTrip = async (id) => {
  const response = await fetch(`${BASE_URL}/${id}`, {
    method: "DELETE",
  });

  if (!response.ok) {
    throw new Error("Error al eliminar el viaje");
  }

  return await response.json();
};

// Actualizar un viaje por ID
const updateTrip = async (id, trip) => {
  const response = await fetch(`${BASE_URL}/${id}`, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(trip),
  });

  if (!response.ok) {
    throw new Error("Error al actualizar el viaje");
  }

  return await response.json();
};

export default {
  addTrip,
  getTrips,
  deleteTrip,
  updateTrip,
};
