import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditTrip = () => {
  const { id } = useParams();
  const navigate = useNavigate(); // Reemplaza useHistory con useNavigate
  const [trip, setTrip] = useState({
    pais: '',
    anio: '',
    mes: '',
    duracion: '',
    costo: '',
    kmRecorrido: ''
  });

  useEffect(() => {
    const fetchTrip = async () => {
      const response = await fetch(`http://localhost:3003/viajes/${id}`);
      const data = await response.json();
      setTrip(data);
    };
    fetchTrip();
  }, [id]);

  const handleChange = (e) => {
    setTrip({ ...trip, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch(`http://localhost:3003/viajes/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(trip)
    });
    if (response.ok) {
      alert('Viaje modificado exitosamente');
      navigate('/view-trips'); // Usar navigate para redirigir
    } else {
      alert('Error modificando el viaje');
    }
  };

  return (
    <div>
      <h2>Modificar Viaje</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="pais" value={trip.pais} onChange={handleChange} required />
        <input type="number" name="anio" value={trip.anio} onChange={handleChange} required />
        <input type="number" name="mes" value={trip.mes} onChange={handleChange} required />
        <input type="number" name="duracion" value={trip.duracion} onChange={handleChange} required />
        <input type="number" name="costo" value={trip.costo} onChange={handleChange} required />
        <input type="number" name="kmRecorrido" value={trip.kmRecorrido} onChange={handleChange} required />
        <button type="submit">Guardar cambios</button>
      </form>
    </div>
  );
};

export default EditTrip;
