// frontend/src/pages/AddTrip.js
import React, { useState } from 'react';
import sendFormSql from '../services/sendFormSql';

const AddTrip = () => {
  const [trip, setTrip] = useState({
    country: '',
    year: '',
    month: '',
    duration: '',
    cost: '',
    km_travelled: '',
    hotel: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTrip({ ...trip, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await sendFormSql.addTrip(trip); // Llamada al backend para agregar viaje
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>País: </label>
      <input type="text" name="country" value={trip.country} onChange={handleChange} required />
      <label>Año: </label>
      <input type="number" name="year" value={trip.year} onChange={handleChange} required />
      <label>Mes: </label>
      <input type="number" name="month" value={trip.month} onChange={handleChange} required />
      <label>Duración: </label>
      <input type="number" name="duration" value={trip.duration} onChange={handleChange} required />
      <label>Costo: </label>
      <input type="number" name="cost" value={trip.cost} onChange={handleChange} required />
      <label>Km Recorridos: </label>
      <input type="number" name="km_travelled" value={trip.km_travelled} onChange={handleChange} required />
      <label>Hotel: </label>
      <input type="text" name="hotel" value={trip.hotel} onChange={handleChange} required />
      <button type="submit">Agregar Viaje</button>
    </form>
  );
};

export default AddTrip;
