import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditTrip = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [trip, setTrip] = useState({
    pais: '',
    anio: '',
    mes: '',
    duracion: '',
    costo: '',
    kmRecorrido: ''
  });

  useEffect(() => {
    const fetchTrip = async () => {
      try {
        const response = await fetch(`http://localhost:3003/viajes/${id}`);
        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }
        const data = await response.json();
        console.log('Viaje obtenido:', data); // Log de los datos obtenidos
        setTrip(data);
      } catch (error) {
        console.error('Error al obtener el viaje:', error); // Log de errores al obtener el viaje
      }
    };
    fetchTrip();
  }, [id]);

  const handleChange = (e) => {
    console.log('Campo cambiado:', e.target.name, 'Nuevo valor:', e.target.value); // Log de cambios de campos
    setTrip({ ...trip, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Formulario enviado con los datos:', trip); // Log de los datos enviados al backend

    try {
      const response = await fetch(`http://localhost:3003/viajes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(trip)
      });

      if (response.ok) {
        console.log('Viaje modificado exitosamente');
        alert('Viaje modificado exitosamente');
        navigate('/view-trips');
      } else {
        const errorText = await response.text();
        console.error('Error modificando el viaje:', errorText); // Log de errores en la respuesta
        alert('Error modificando el viaje');
      }
    } catch (error) {
      console.error('Error en la solicitud de modificación:', error); // Log de errores en la solicitud
    }
  };

  return (
    <div>
      <h2>Modificar Viaje</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="pais"
          value={trip.pais}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="anio"
          value={trip.anio}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="mes"
          value={trip.mes}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="duracion"
          value={trip.duracion}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="costo"
          value={trip.costo}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="kmRecorrido"
          value={trip.kmRecorrido}
          onChange={handleChange}
          required
        />
        <button type="submit">Guardar cambios</button>
      </form>
    </div>
  );
};

export default EditTrip;
